import styled from 'styled-components';

const Button = styled.button`
  background: transparent;
  color: blue;
  border-color: blue;
  border-radius: 6px;
  margin: 0 1reml;
  padding: 0.25em 1em;
  font-size: 1.5rem;
  margin: 1rem;

  &&:hover {
    transition: 0.3s;
    background: blue;
    color: white;
  }
`;

export default Button;
