import styled from 'styled-components';

const Button = styled.button`
  background: transparent;
  color: blue;
  border-color: blue;
  border-radius: 6px;
  margin: 0 1reml;
  padding: 0.25em 1em;
  font-size: 2rem;
  margin: 2rem;
`;

export default Button;
