import { useState, useEffect } from 'react';

import EditCustomer from './components/EditCustomer';
import CustomerList from './components/CustomerList';
import NavBar from './components/Layout/NavBar';
import Container from './components/UI/Container';
import Button from './components/UI/Button';

function App() {
  const [customers, setCustomers] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const customersLength = Object.keys(customers).length;

  async function fetchCustomersHandler() {
    setIsLoading(true);
    setError(null);

    try {
      const response = await fetch(
        'https://rawgit.com/wvchallenges/se-exp-challenge-invoice/master/settings.json'
      );
      if (!response.ok) {
        throw new Error('Something went wrong.');
      }

      const data = await response.json();

      setCustomers(data.customers);
      setIsLoading(false);
    } catch (error) {
      setError(error.message);
      setIsLoading(false);
    }
  }

  useEffect(() => {});

  let content = <p>Found no movies.</p>;

  if (customers.length > 0) {
    content = <CustomerList customers={customers} />;
  }

  if (error) {
    content = { error };
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }

  return (
    <>
      <NavBar />
      <Container>
        {content}
        {/* <EditCustomer /> */}
        <Button onClick={fetchCustomersHandler}>Fetch Customers</Button>
      </Container>
    </>
  );
}

export default App;
