import Button from './UI/Button';
import useInput from '../hooks/use-input';

import classes from './EditCustomer.module.css';

const EditCustomer = () => {
  const {
    value: enteredName,
    valueIsValid: nameIsValid,
    hasError: nameHasError,
    valueChangeHandler: nameChangeHandler,
    valueBlurHandler: nameBlurHandler,
    reset: resetName,
  } = useInput(value => value.trim() === !"");

  const {
    value: enteredEmail,
    valueIsValid: emailIsValid,
    hasError: emailHasError,
    valueChangeHandler: emailChangeHandler,
    valueBlurHandler: emailBlurHandler,
    reset: resetEmail,
  } = useInput(value => value);

  const {
    value: enteredChannel,
    valueIsValid: channelIsValid,
    hasError: channelHasError,
    valueChangeHandler: channelChangeHandler,
    valueBlurHandler: channelBlurHandler,
    reset: resetChannel,
  } = useInput(value => value);

  const {
    value: enteredAddress,
    valueIsValid: addressIsValid,
    hasError: addressHasError,
    valueChangeHandler: addressChangeHandler,
    valueBlurHandler: addressBlurHandler,
    reset: resetAddress,
  } = useInput(value => value);

  const {
    value: enteredPostalCode,
    valueIsValid: postalCodeIsValid,
    hasError: postalCodeHasError,
    valueChangeHandler: postalCodeChangeHandler,
    valueBlurHandler: postalCodeBlurHandler,
    reset: resetPostalCode,
  } = useInput(value => value);

  const {
    value: enteredCity,
    valueIsValid: cityIsValid,
    hasError: cityHasError,
    valueChangeHandler: cityChangeHandler,
    valueBlurHandler: cityBlurHandler,
    reset: resetCity,
  } = useInput(value => value);

  const {
    value: enteredProvince,
    valueIsValid: provinceIsValid,
    hasError: provinceHasError,
    valueChangeHandler: provinceChangeHandler,
    valueBlurHandler: provinceBlurHandler,
    reset: resetProvince,
  } = useInput(value => value);

  const formSubmitHandler = e => {};

  return (
    <form onSubmit={formSubmitHandler}>
      <div className={classes.form}>
        <div>
          <label>Name</label>
          <br />
          <input
            type='text'
            id='name'
            onChange={nameChangeHandler}
            onBlur={nameBlurHandler}
            value={enteredName}
          />
        </div>
        <div>
          <label>Email</label>
          <br />
          <input
            type='email'
            id='email'
            onChange={emailChangeHandler}
            onBlur={emailBlurHandler}
            value={enteredEmail}
          ></input>
        </div>
        <div>
          <label>Channel</label>
          <br />
          <input
            type='text'
            id='channel'
            onChange={channelChangeHandler}
            onBlur={channelBlurHandler}
            value={enteredChannel}
          ></input>
        </div>
        <div>
          <label>Address</label>
          <br />
          <input
            type='text'
            id='address'
            onChange={addressChangeHandler}
            onBlur={addressBlurHandler}
            value={enteredAddress}
          ></input>
        </div>
        <div>
          <label>Postal Code</label>
          <br />
          <input
            type='text'
            id='postal-code'
            onChange={postalCodeChangeHandler}
            onBlur={postalCodeBlurHandler}
            value={enteredPostalCode}
          ></input>
        </div>
        <div>
          <label>City</label>
          <br />
          <input
            type='text'
            id='city'
            onChange={cityChangeHandler}
            onBlur={cityBlurHandler}
            value={enteredCity}
          ></input>
        </div>
        <div>
          <label>Province</label>
          <br />
          <input
            type='text'
            id='province'
            onChange={provinceChangeHandler}
            onBlur={provinceBlurHandler}
            value={enteredProvince}
          ></input>
        </div>
      </div>
      <Button>Submit</Button>
    </form>
  );
};

export default EditCustomer;
